int makeCount(int wAquatics,int wAthletics,int wTeam,int wCombat,int wWeapons,int wRaquet,int wRiding,int wGym,int wMultiple,int wWater,int wSwimming,int wDiving,int wMarathonSwim,int wPolo,int wSprints,int wDistance,int wField,int wLifting,int wJumps,int wWalking,int wBasket,int wFoot,int wHand,int wHockey,int wVolley,int wBeach,int wJudo,int wTaekwondo,int wFencing,int wWrestling,int wBoxing,int wArchery,int wPistol,int wRifle,int wSkeet,int wTrap,int wTennis,int wBad,int wTT,int wCycling,int wEquestrian,int wEvents,int wTeamGym,int wAround,int wTramp,int wPentathlon,int wTriathlon,int wCanoeSlalom,int wCanoeSprint,int wRowing,int wSailing) {
	int i = 0; int ii; int iii;
	for (i=0;i<ncountries;i++){allcount[i]=0;goldcount[i]=0;silvercount[i]=0;bronzecount[i]=0;topcount[i]=0;}
	
	const int firstweight[] = {wAquatics,wAthletics,wTeam,wCombat,wWeapons,wRaquet,wRiding,wGym,wMultiple,wWater};
	const int secondweight[] = {wSwimming,wDiving,wMarathonSwim,wPolo,wSprints,wDistance,wField,wLifting,wJumps,wWalking,wBasket,wFoot,wHand,wHockey,wVolley,wBeach,wJudo,wTaekwondo,wFencing,wWrestling,wBoxing,wArchery,wPistol,wRifle,wSkeet,wTrap,wTennis,wBad,wTT,wCycling,wEquestrian,wEvents,wTeamGym,wAround,wTramp,wPentathlon,wTriathlon,wCanoeSlalom,wCanoeSprint,wRowing,wSailing};
	int firstw = 0; int secondw = 0; int thirdw = 0; int medalw = 0; int countryid = 0; int amid = 0; int aamid = 0;
	for (i=0;i<nmedals-1;i++){
		firstw = allmedals[i*6];
		secondw = allmedals[i*6+1];
		thirdw = allmedals[i*6+2]+1;
		medalw = allmedals[i*6+5]+1;
		countryid = allmedals[i*6+3];
		
		allcount[countryid]+=(medalw)*firstweight[firstw]*secondweight[secondw]*100/(thirdw); 

		if (allmedals[i*6+5]==2){goldcount[countryid]+=firstweight[firstw]*secondweight[secondw]*100/(thirdw);} 
		if (allmedals[i*6+5]==1){silvercount[countryid]+=firstweight[firstw]*secondweight[secondw]*100/(thirdw);} 
		if (allmedals[i*6+5]==0){bronzecount[countryid]+=firstweight[firstw]*secondweight[secondw]*100/(thirdw);}

	}

	for (i=0;i<ncountries;i++){if (allcount[i]>maxvalue){maxvalue=allcount[i];}}
	for (i=0;i<ncountries;i++){
		for (ii=0;ii<ncountries;ii++){
			if(allcount[i]>topcount[ii]){
				for(iii=ncountries-1;iii>ii;iii--){topcount[iii]=topcount[iii-1];topcountry[iii]=topcountry[iii-1];}
				topcount[ii]=allcount[i]; topcountry[ii]=i; break;

	}}}
	
    return allcount[10];
}

double sendColor(int i){
	int logornot = 1;
	int logadjust = log2100(maxvalue/100)/100;
	if (logornot==1){return (log2100(allcount[i]+pow2(logadjust))-logadjust*100)*255/(log2100(maxvalue+pow2(logadjust))-logadjust*100);}
	else {return allcount[i]*255/maxvalue;}
}

